package input;import enums.CommandWord;public class CommandWords {  private static final String[] validCommands = {      "go", "use", "quit", "help", "inspect", "take", "drop", "inventory", "battle"  };  /**   * Find the enums.CommandWord associated with a command word. The enums.CommandWord corresponding to text   * string, or null if it is not a valid command word.   */  public CommandWord getCommandWord(String aString) {    int i = 0;    for (CommandWord command : CommandWord.values()) {      if (validCommands[i].equals(aString)) {        return command;      }      i++;    }    return null;  }  public void showAll() {    for (String validCommand : validCommands) {      System.out.print(validCommand + ", ");    }    System.out.println();  }}